import React, { useEffect, useState } from 'react';
import { Container, Table, Row, Col, Button, } from 'react-bootstrap'
import Header from '../../components/header';
import { useDispatch, useSelector } from 'react-redux';
import Modal from '../../components/model';
import Input from '../../components/input'
const Home = (props) => {

  const tasks = useSelector(state => state.tasks);
  const [name, setName] = useState('')
  const [show, setShow] = useState(false);
  // function to render the add new task model
  const handleClose = () => {

    setShow(false);
  }

  // function to show the add new task
  const handleShow = () => setShow(true);

  const renderAddTaskModal = () => {
    return (
      <Modal
        show={show}
        handleClose={handleClose}
        modalTitle={'Add new Task'}
      >
                <Input
                    label="Name"
                    placeholder="Product Name"

                    value={name}
                    onChange={(e) => setName(e.target.value)}

                />
                <FormGroup controlId="date" bsSize="large">
      <ControlLabel>Birthday</ControlLabel>
      <FormControl
        type="date"
        style={{width:'100%'}}
      />
    </FormGroup>
                  
      </Modal>
    );
  }

  return (
    <>
      <Header />
      <Container>
        <Row>

          <Col md={{ offset: 8 }}><Button variant="primary" onClick={handleShow}>Add a new Task</Button>{' '}</Col>
        </Row>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Task Name</th>
              <th>States</th>

            </tr>
          </thead>
          <tbody>

            {tasks.tasks.map((data, index) => {
              return (
                <tr >
                  <td>{++index}</td>
                  <td>{data.title}</td>
                  <td>{data.activeStatus}</td>

                </tr>
              );
            })}
          </tbody>
        </Table>
     
      </Container>
      {renderAddTaskModal()}

    </>
  )
}

export default Home
