import React, { useEffect, useState } from 'react';
import { Container, Table, Row, Col, Button } from 'react-bootstrap'
import Header from '../../components/header';
import { useDispatch, useSelector } from 'react-redux';
const Home = (props) => {

  const tasks = useSelector(state => state.tasks);
  const [show, setShow] = useState(false);
  // function to render the add new task model
  const handleClose = () => {
    // sending data as a form because it has an image
   

    setShow(false);
}

 // function to show the add new model
 const handleShow = () => setShow(true);

  const renderAddTaskModal = () => {
    return (
      <Modal
        show={show}
        handleClose={handleClose}
        modalTitle={'Add new Product'}
      >

        body

      </Modal>
    );
  }



  return (
    <>

      <Header />
      <Container>
        <Row>
 
          <Col md={{ offset: 8 }}><Button variant="primary" onClick={handleShow}>Add a new Task</Button>{' '}</Col>
        </Row>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Task Name</th>
              <th>States</th>

            </tr>
          </thead>
          <tbody>

            {tasks.tasks.map((data, index) => {
              return (
                <tr >
                  <td>{++index}</td>
                  <td>{data.title}</td>
                  <td>{data.activeStatus}</td>

                </tr>
              );
            })}
          </tbody>
        </Table>
      </Container>
      {renderAddTaskModal()}
    </>
  )
}

export default Home
