import React, { useEffect, useState } from 'react';
import { Container, Table, Row, Col, Button, FormGroup, FormControl } from 'react-bootstrap'
import Header from '../../components/header';
import { useDispatch, useSelector } from 'react-redux';
import Modal from '../../components/model';
import Input from '../../components/input';
import { addTask, deleteTaskById } from '../../actions'

const Home = (props) => {

  const tasks = useSelector(state => state.tasks);
  const [title, setTitle] = useState('')
  const [endDate, setEndDate] = useState('')

  const [show, setShow] = useState(false);
  const dispatch = useDispatch();


  // function to colose the model
  const handleClose = () => {
    setShow(false);
  }


  //form submit function
  const submitForm = () => {

    const payload = {
      title,
      endDate,

    };

    dispatch(addTask(payload));
    //console.log(payload);

    setShow(false);

  }

  const deleteTask = (taskId) => {



    const payload = {
      taskId
    };
    alert('Are you sure you want to delete this task?')
    dispatch(deleteTaskById(payload));
  };



  // function to show the add new task
  const showAddNewModel = () => setShow(true);

  //const handleShow = () => setShow(true);


  const renderAddTaskModal = () => {
    return (
      <Modal
        show={show}
        handleClose={handleClose}
        modalTitle={'Add new Task'}
        onSubmit={submitForm}
      >
        <Input
          label="Name"
          placeholder="Product Name"

          value={title}
          onChange={(e) => setTitle(e.target.value)}

        />
        <FormGroup controlId="date" bsSize="large">

          <FormControl
            type="date"
            style={{ width: '100%' }}

            value={endDate}
            onChange={(e) => setEndDate(e.target.value)}
          />
        </FormGroup>

      </Modal>
    );
  }

  return (
    <>
      <Header />
      <Container>
        <Row>

          <Col md={{ offset: 8 }}><Button variant="primary" onClick={showAddNewModel}>Add a new Task</Button>{' '}</Col>
        </Row>
        <Table striped bordered hover>
          <thead>
            <tr>
              <th>#</th>
              <th>Task Name</th>
              <th>States</th>
              <th>End date</th>
              <th>Remove</th>
            </tr>
          </thead>
          <tbody>

            {tasks.tasks.map((data, index) => {
              return (
                <tr >
                  <td>{++index}</td>
                  <td>{data.title}</td>
                  <td>{data.activeStatus}</td>
                  <td>{data.endDate}</td>
                  <Button variant="danger" onClick={() => deleteTask(data._id)} >Remove This Task</Button>
                </tr>
              );
            })}
          </tbody>
        </Table>

      </Container>
      {renderAddTaskModal()}

    </>
  )
}

export default Home
